import React, { Component } from 'react';

import { View, Text, SafeAreaView } from 'react-native';

import { useTailwind } from 'tailwind-rn'
/* eslint-disable-next-line */

const Home = () => {
  const tailwind = useTailwind();
  return (
    <SafeAreaView style={tailwind('h-full')}>
      <View style={t("h-64 bg-gray-400 items-center justify-center")}>
        <View style={t("justify-center")}>
          <Text style={t("text-2xl font-bold")}>Welcome</Text>
          <Text style={t("text-lg")}>
            Millions of movies, TV shows and people to discover.
          </Text>
          <Text style={t("text-lg")}>Explore now.</Text>
        </View>
        <View style={t("flex-row mt-5")}>
          <View style={t("h-12 w-screen bg-gray-700 rounded-full ")}>
            <Text style={t("text-center text-white")} />
            <View
              style={t(
                "h-12 w-20 bg-gray-600 text-white font-bold rounded-full self-end"
              )}
            />
          </View>
        </View>
      </View>
    </SafeAreaView>
  );
}

export default Home;
